css:
body {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width:100vw;
    background-color: #eae2b7;
    /* Neutral background to highlight the certificate */
    font-family: 'Open Sans', sans-serif;
}

.certificate {
    width: 60%;
    background: white;
    /* Keeps the background neutral to ensure the logo stands out */
    text-align: center;
    padding: 40px;
    box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.15);
    border-radius: 10px;
    position: relative;
    border: 5px solid #aacc00;
    display: flex;
    justify-content: center;
    align-items: center;
    /* Light green border */
}

.image{
    flex-direction: row;
    display: flex;
    justify-content: center;

}

.content{
    flex-direction: row;
}


.logo {
    width: 300px;
    top: 20px;
    left: 20px;
}

h1 {
    font-family: 'Merriweather', serif;
    color: #d00000;
    /* Dark red for main title */
    font-size: 28px;
    margin-bottom: 10px;
}

p {
    color: #005f73;
    /* Subdued color for text to maintain elegance */
    font-size: 18px;
    margin: 5px 0;
}

h2 {
    color: #048b9a;
    /* Dark green to make the name pop */
    font-size: 24px;
    margin: 20px 0;
}

h3 {
    color: #9a031e;
    /* Darker red for emphasis */
    font-size: 20px;
    margin-bottom: 20px;
}

h4 {
    color: #fb8b24;
    /* Warm tone for date to create a subtle highlight */
    font-size: 16px;
    margin-bottom: 40px;
}

.signature {
    font-size: 16px;
    color: #444;
    /* Neutral for readability */
    margin-top: 20px;
}

#sign{
    height: 50px;
    margin-bottom: -25px;
}
